var moved = false;
var arrowtimeout;
var titletimeout;
var timebartimeout;

$(document).ready(function(){
  $( "#body" ).animate({
    backgroundColor: "#000000",
  },2000);

  setTimeout(function(){
    $( "#arrowL" ).animate({
      opacity: 0
    },2000);
    $( "#arrowR" ).animate({
      opacity: 0
    },2000);
    moved = false;
  }, 4000);

  window.onmousemove = function(e){
    if(!moved){
      loadTitle();
      animateTitle();
      animateArrow();
      animateTimeBar();
      moved = true;
    }else{
      clearTimeout(arrowtimeout);
      clearTimeout(titletimeout);
      clearTimeout(timebartimeout);
      arrowtimeout = setTimeout(function(){
        $( "#arrowL" ).animate({
          opacity: 0
        },1000);
        $( "#arrowR" ).animate({
          opacity: 0
        },1000);
        moved = false;
      }, 2000);
      titletimeout = setTimeout(function(){
        $( "#title" ).animate({
          opacity: 0
        },1000);
      }, 2000);
      timebartimeout = setTimeout(function(){
        $( "#timebar" ).animate({
          opacity: 0
        },{
          step: function( now, fx ) {
            $( "#timebar" ).width(getVideoProgress()+'%');
          }
        }, 1000
        );
      }, 2000);
    }
  };
});



$(document.activeElement).keydown(function(e) {
  if(!initialized) return;
  switch(e.which) {
    case 37: // left
      previousVideo();
      animateTitle();
      break;

    case 39: // right
      nextVideo();
      animateTitle();
      break;

    default: return; // exit this handler for other keys
}
  e.preventDefault(); // prevent the default action (scroll / move caret)
});


function mouseMove(){

}



function mouseClick(){
  togglePlayPause();
}

function arrowLeftClick(){
  previousVideo();
  loadTitle();
  animateTitle();
}
function arrowRightClick(){
  nextVideo();
  loadTitle();
  animateTitle();
}



function titleClick(){
  if(!initialized) return;
  pauseVideo();
  window.open("https://www.youtube.com/watch?v=" + queue[currindex][0]);
}

function loadTitle(){
  if(!initialized) return;
  var vidtitle = queue[currindex][1];
  $( "#title" ).text(vidtitle);
}

function animateTitle(){
  clearTimeout(titletimeout);
  $( "#title" ).animate({
    opacity: 0.75,
  },500);
  clearTimeout(titletimeout);
  titletimeout = setTimeout(function(){
    $( "#title" ).animate({
      opacity: 0
    },1000);
  }, 2000);

}

function animateArrow(){
  $( "#arrowL" ).animate({
    opacity: 0.5
  },500);
  $( "#arrowR" ).animate({
    opacity: 0.5
  },500);
  clearTimeout(arrowtimeout);
  arrowtimeout = setTimeout(function(){
    $( "#arrowL" ).animate({
      opacity: 0
    },1000);
    $( "#arrowR" ).animate({
      opacity: 0
    },1000);
  }, 2000);
}


function animateTimeBar(){
  $( "#timebar" ).animate({
    opacity: 0.5
  },500);
  clearTimeout(timebartimeout);
  timebartimeout = setTimeout(function(){
    $( "#timebar" ).animate({
      opacity: 0
    },1000);
  }, 2000);
}




