


// 2. This code loads the IFrame Player API code asynchronously.
var tag = document.createElement('script');

tag.src = "https://www.youtube.com/iframe_api";
var firstScriptTag = document.getElementsByTagName('script')[0];
firstScriptTag.parentNode.insertBefore(tag, firstScriptTag);

// 3. This function creates an <iframe> (and YouTube player)
//    after the API code downloads.
var currindex;
var curr;
var prev;
var next;
var temp;
var currdisp = "#curr";
var nextdisp = "#next";
var prevdisp = "#prev";

var initialized = false;
function initIFrames(){
  initialized = true;
  currindex = 0;
  loadTitle();
  animateTitle();
  curr = new YT.Player('curr', {
    height: '390',
    width: '640',
    videoId: queue[0][0],
    playerVars: {
      'autoplay': 1,
      'showinfo': 0,
      'controls': 0,
      'iv_load_policy': 3,
      'disablekb': 1
    },
    events: {
      'onReady': onPlayerReady,
      'onError': onPlayerError,
      'onStateChange': onPlayerStateChange
    }
  });

  prev = new YT.Player('prev', {
    height: '390',
    width: '640',
    videoId: queue[0][0],
    playerVars: {
      'autoplay': 1,
      'showinfo': 0,
      'controls': 0,
      'iv_load_policy': 3,
      'disablekb': 1
    },
    events: {
      'onReady': onPlayerReady,
      'onError': onPlayerError,
      'onStateChange': onPlayerStateChange
    }
  });

  next = new YT.Player('next', {
    height: '390',
    width: '640',
    videoId: queue[1][0],
    playerVars: {
      'autoplay': 1,
      'showinfo': 0,
      'controls': 0,
      'iv_load_policy': 3,
      'disablekb': 1
    },
    events: {
      'onReady': onPlayerReady,
      'onError': onPlayerError,
      'onStateChange': onPlayerStateChange
    }
  });
}

function onYouTubeIframeAPIReady() {
  loadVideos().always(function() { //is returned as deffered object
    try {
      initIFrames();
    }catch(err) {
      alert(err.message);
    }
  });
}

// 4. The API will call this function when the video player is ready.
function onPlayerReady(event) {
  if(!initialized) return;
  event.target.playVideo();
}


function onPlayerError(event) {
  if(!initialized) return;
  if(event.target == curr){
    queue.splice(currindex,1);
    curr.loadVideoById({'videoId': queue[currindex][0], 'startSeconds': 0, 'suggestedQuality': 'large'});
    next.loadVideoById({'videoId': queue[currindex+1][0], 'startSeconds': 0, 'suggestedQuality': 'large'});
  }
  if(event.target == next){
    queue.splice(currindex+1,1);
    next.loadVideoById({'videoId': queue[currindex+1][0], 'startSeconds': 0, 'suggestedQuality': 'large'});
  }
}
// 5. The API calls this function when the player's state changes.
//    The function indicates that when playing a video (state=1),
//    the player should play for six seconds and then stop.
var done = false;
function onPlayerStateChange(event) {
  if(!initialized) return;
  if(event.data == YT.PlayerState.UNSTARTED){
    event.target.playVideo();
  }
  if (event.data == YT.PlayerState.PLAYING) {
    prev.pauseVideo();
    next.pauseVideo();
  }
  if (event.data == YT.PlayerState.ENDED)
    nextVideo();
}
function stopVideo() {
  player.stopVideo();
}

function nextVideo() {
  if(!initialized) return;
  if(currindex == queue.length-1){
    currindex = 0;
    curr.loadVideoById({'videoId': queue[currindex][0], 'startSeconds': 0, 'suggestedQuality': 'large'});
    next.loadVideoById({'videoId': queue[currindex+1][0], 'startSeconds': 0, 'suggestedQuality': 'large'});
    return;
  }
  curr.pauseVideo();

  temp = prev;
  prev = curr;
  curr = next;
  next = temp;
  
  curr.playVideo();

  currindex++;
  if( currindex < queue.length - 1)
    next.loadVideoById({'videoId': queue[currindex+1][0], 'startSeconds': 0, 'suggestedQuality': 'large'});

  prev.seekTo(0);
  next.seekTo(0);

  temp = prevdisp;
  prevdisp = currdisp;
  currdisp = nextdisp;
  nextdisp = temp;
  $( currdisp ).css( "display", "block" );
  $( nextdisp ).css( "display", "none" );
  $( prevdisp ).css( "display", "none" );

  if(currindex > queue.length - 15)
    loadVideos();

  loadTitle();
  animateTimeBar();
}

function previousVideo() {
  if(!initialized) return;
  if (currindex == 0) return;
  curr.pauseVideo();

  temp = next;
  next = curr;
  curr = prev;
  prev = temp;

  curr.playVideo();

  currindex--;
  if( currindex > 0)
    prev.loadVideoById({'videoId': queue[currindex-1][0], 'startSeconds': 0, 'suggestedQuality': 'large'});

  prev.seekTo(0);
  next.seekTo(0);

  temp = nextdisp;
  nextdisp = currdisp;
  currdisp = prevdisp;
  prevdisp = temp;

  $( currdisp ).css( "display", "block" );
  $( nextdisp ).css( "display", "none" );
  $( prevdisp ).css( "display", "none" );

  loadTitle();
  animateTimeBar();
}




function togglePlayPause(){
  if(curr===null || !initialized) return;
  if(curr.getPlayerState()==2)
    curr.playVideo();
  else
    curr.pauseVideo();
}

function pauseVideo(){
  if(!initialized) return;
  curr.pauseVideo();
}


function getVideoProgress(){
  if(!initialized) return 0;
  var totaltime = curr.getDuration();
  var nowtime = curr.getCurrentTime();
  return totaltime > 0 ? 100 * nowtime / totaltime : 0;
}

function skipVideoTo(index){
  if(!initialized) return 0;
  var totaltime = curr.getDuration();
  var nowtime = curr.getCurrentTime();
  if(index == -1){    //rewind
    index = Math.round(10*nowtime/totaltime) - 1;
  }
  if(index == -2){    //fastforward
    index = Math.round(10*nowtime/totaltime) + 1;
  }
  if(index == -1){
    if(currindex == 0){
      curr.seekTo(0);
    }
    previousVideo();
    animateTitle();
    return;
  }
  if(index == 10){
    nextVideo();
    animateTitle();
    return;
  }
  curr.seekTo(index * totaltime / 10);
}


