var reddit = require("./RedditVideos.js");
var yttrends = require("./YTTrendsVideos.js");
var users = require("./Users.js");
var mongodb = require("./MongoDB.js");

module.exports = {
  getRedditJSON: function (subreddit) {
    return reddit.getRedditJSON(subreddit);
  },

  getRedditVideoJSON: function(videoId, resfn){
    return reddit.getRedditVideoJSON(videoId, resfn);
  },

  getYTTrendsJSON: function() {
    return yttrends.getYTTrendsJSON();
  },

  addChannel: function(channel){
    reddit.addUserChannel(channel);
  },

  getUserJSON: function(userId, resfn){
    users.getUserJSON(userId, resfn);
  },

  updateUserLikedVideos: function(userId, updown, video){
    users.updateUserLikedVideos(userId, updown, video);
  },

  getUserById: function(userId, resfn){
    mongodb.getUserFromDB(userId, resfn);
  }
};