var reddit = require("./RedditVideos.js");
var yttrends = require("./YTTrendsVideos.js");

module.exports = {
  getRedditJSON: function (subreddit) {
	return reddit.getRedditJSON(subreddit);
  },

  getRedditVideoJSON: function(videoId){
	return reddit.getRedditVideoJSON(videoId);
  },

  getYTTrendsJSON: function () {
	return yttrends.getYTTrendsJSON();
  },

  addChannel: function(channel){
	reddit.addUserChannel(channel);
  }
};